mapper.select:

 	select    

   	from tb_project


		 	and id = #{id} 


		 	and name = #{name} 


		 	and description = #{description} 


		 	and sponsor = #{sponsor} 


		 	and upload_date = #{uploadDate} 


		 	and checker = #{checker} 


		 	and check_date = #{checkDate} 


		 	and status = #{status} 


		 	and check_msg = #{checkMsg} 


		 	and begin_datetime = #{beginDatetime} 


		 	and end_datetime = #{endDatetime} 


		 	and attender_limit = #{attenderLimit} 


		 	and plan_doc = #{planDoc} 


		 	and foster_doc = #{fosterDoc} 


		 	and talents_doc = #{talentsDoc} 


		 	and process_status = #{processStatus}

