mapper.select:

 	select    

   	from tb_resources


		 	and id = #{id} 


		 	and resName = #{resname} 


		 	and resDescribe = #{resdescribe} 


		 	and resLabel = #{reslabel} 


		 	and resPath = #{respath} 


		 	and pdfPath = #{pdfpath} 


		 	and swfPath = #{swfpath} 


		 	and resFormat = #{resformat} 


		 	and resSrc = #{ressrc} 


		 	and resType = #{restype} 


		 	and dicType = #{dictype} 


		 	and uploaderId = #{uploaderid} 


		 	and uploader = #{uploader} 


		 	and uploadDate = #{uploaddate} 


		 	and modifier = #{modifier} 


		 	and modifyDate = #{modifydate} 


		 	and clickTimes = #{clicktimes} 


		 	and downloadTimes = #{downloadtimes} 


		 	and resStar = #{resstar} 


		 	and resStatus = #{resstatus} 


		 	and pState = #{pstate}

