	)
    values (
    	#{id ,jdbcType=BIGINT} , #{resname ,jdbcType=VARCHAR} , #{resdescribe ,jdbcType=VARCHAR} , #{reslabel ,jdbcType=VARCHAR} , #{respath ,jdbcType=VARCHAR} , #{pdfpath ,jdbcType=VARCHAR} , #{swfpath ,jdbcType=VARCHAR} , #{resformat ,jdbcType=VARCHAR} , #{ressrc ,jdbcType=VARCHAR} , #{restype ,jdbcType=TINYINT} , #{dictype ,jdbcType=TINYINT} , #{uploaderid ,jdbcType=BIGINT} , #{uploader ,jdbcType=VARCHAR}, #{uploaddate} , #{modifier ,jdbcType=VARCHAR}, #{modifydate} , #{clicktimes ,jdbcType=INTEGER} , #{downloadtimes ,jdbcType=INTEGER} , #{resstar ,jdbcType=TINYINT} , #{resstatus ,jdbcType=TINYINT} , #{pstate ,jdbcType=INTEGER}

